      if (event.shiftKey) {
        if (currentTabPosition > 0) {
          currentTabPosition -= 1;
        } else {
          currentTabPosition = (this.tabableElements.length - 1);
        }
      } else {
        if (currentTabPosition >= (this.tabableElements.length - 1)) { //last element of array
          currentTabPosition = 0;
        } else {
          currentTabPosition += 1;
        }
      }

