          if (!widget) {
            return picker;
          }
          // Ignore event if in the middle of a picker transition
          widget.find('.binf-collapse').each(function () {
            var collapseData = $(this).data('collapse');
            if (collapseData && collapseData.transitioning) {
              transitioning = true;
              return false;
            }
            return true;
          });
          if (transitioning) {
            return picker;
          }
          if (component && component.hasClass('binf-btn')) {
            component.toggleClass('binf-active');
          }
          widget.hide();
          
          $(window).off('resize', hide);
          widget.off('click touchend', '[data-action]');
          widget.off('mousedown touchstart', false);

          widget.remove();
          widget = false;

          notifyEvent({
            type: 'dp.hide',
            date: date.clone()
          });
          return picker;
